let xmlString = '';

if (typeof $json.body === 'string') {
  xmlString = $json.body;
} else if ($json.body && typeof $json.body === 'object') {
  xmlString = $json.body.data || $json.body.xml || JSON.stringify($json.body);
} else if (typeof $json.data === 'string') {
  xmlString = $json.data;
} else if (typeof $json === 'string') {
  xmlString = $json;
} else {
  xmlString = $json.xml || JSON.stringify($json);
}

const entryRegex = /<entry[^>]*>([\s\S]*?)<\/entry>/gi;
const entries = [];
let match;

while ((match = entryRegex.exec(xmlString)) !== null) {
  entries.push(match[1]);
}

const posts = [];

for (let i = 0; i < entries.length; i++) {
  const entryContent = entries[i];

  const titleMatch = entryContent.match(/<title[^>]*>(.*?)<\/title>/is);
  const title = titleMatch ? titleMatch[1]
    .replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1')
    .trim() : '';

  let link = '';
  const linkHrefMatch = entryContent.match(/<link[^>]*href=["']([^"']+)["'][^>]*>/i);
  if (linkHrefMatch) {
    link = linkHrefMatch[1].trim();
  } else {
    const linkContentMatch = entryContent.match(/<link[^>]*>(.*?)<\/link>/is);
    if (linkContentMatch) {
      link = linkContentMatch[1]
        .replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1')
        .trim();
    }
  }

  if (title && link) {
    posts.push({ title, link });
  }
}

const formattedPosts = posts.map((post, index) => {
  return `${index + 1}. ${post.title} - ${post.link}`;
}).join('\n');

return {
  json: {
    reddit_posts: formattedPosts
  }
};
